graphics.vertex_shader:
#version 330 core
layout(location = 0) in vec2 aPos;
layout(location = 1) in vec2 aTexCoord;
out vec2 TexCoord;
uniform mat4 model;
uniform mat4 projection;
uniform float currentFrame;
uniform float atlasColumns;
uniform float atlasRows;
void main(){
int frame = int(currentFrame);
TexCoord = vec2(
(aTexCoord.x + float(frame % int(atlasColumns))) / atlasColumns,
(aTexCoord.y + float(frame / int(atlasColumns))) / atlasRows
);
gl_Position = projection * model * vec4(aPos, 0.0, 1.0);}

graphics.fragment_shader:
#version 330 core
out vec4 color;
in vec2 TexCoord;
uniform sampler2D texture1;
void main() {
vec4 texColor = texture(texture1, TexCoord);
if(texColor.a < 0.7) discard;
color = texColor;
}

